  public function town3 () {
    foreach ($this->town_infos as $town_info) {

      if (!(($cate = TownCategory::find ('one', array ('conditions' => array ('name = ?', $town_info['cate_name'])))) || verifyCreateOrm ($cate = TownCategory::create (array ('name' => $town_info['cate_name'])))))
        exit ('TownCategory 錯誤！');

      if (Town::find ('one', array ('conditions' => array ('town_category_id = ? AND name = ?', $cate->id, $town_info['town_name']))))
        exit ('Town find 錯誤！');

      if (!(verifyCreateOrm ($town = Town::create (array ('town_category_id' => $cate->id, 'name' => $town_info['town_name'], 'postal_code' => $town_info['postal_code'], 'cwb_town_id' => $town_info['cwb_town_id'], 'latitude' => $town_info['latitude'], 'longitude' => $town_info['longitude']))) && ($town->put_pic ())))
        exit ('Town create 錯誤！');

      if (!verifyCreateOrm ($bound = TownBound::create (array ('town_id' => $town->id, 'northeast_latitude' => $town_info['northeast_latitude'], 'northeast_longitude' => $town_info['northeast_longitude'], 'southwest_latitude' => $town_info['southwest_latitude'], 'southwest_longitude' => $town_info['southwest_longitude']))))
        exit ('TownBound create 錯誤！');

      echo "完成 " . $town_info['cate_name'] . " " . $town_info['town_name'] . "\n";
      sleep (1);
    }
  }


