        echo " Create a GoalTag, id: " . $tag->id . "\n";
    }

    $tag_count = GoalTag::count ();

    $times = range (1, rand (50, 100));
    echo "\n 新增 " . count ($times) . "筆 Goal\n==========================================\n";
    foreach ($times as $time) {
      $user = User::find ('one', array ('select' => 'id', 'order' => 'RAND()', 'conditions' => array ()));
      $params = array (
          'user_id' => $user->id,
